import styled from "styled-components";

export const ModalStyled = styled.div`
height: 100vh;
width: 100vw;
background-color: rgba(0,0,0,0.4);
display: flex;
align-items: center;
justify-content: center;
position: fixed;
overflow: hidden;
`

export const ModalContentStyled = styled.div`
width: 200px;
height: 200px;
background-color: white;
`
