import styled from "styled-components";

export const ChooseCardStyled = styled.li`
list-style-type: none;

background-color: #0E8784;

margin-top: 24px;
margin-left: 24px;
margin-right: 24px;

border-radius: 8px;
`

export const ChooseCardImageBox = styled.div`
display: flex;
justify-content: center;
align-items: center;

padding-top: 72px;
`
export const ChooseCardHeader = styled.h3`
font-family: Fraunces;
text-align: center;
font-size: 24px;
line-height: 1.34;
font-weight: 900;
color: #FEFCF7;

margin-top: 56px;
`

export const ChooseCardText = styled.p`
font-family: Barlow;
text-align: center;
font-size: 15px;
line-height: 1.67;
font-weight: 900;
color: #FEFCF7;
`
