import styled from "styled-components";

export const ChooseBox = styled.div`
padding-top: 120px;
position: relative;
`
export const ChooseColorBox = styled.div`
position: absolute;

z-index: -1;
width: 100%;
height: 902px;
background-color: #2C343E;
border-radius: 10px;
`

export const ChooseHeaderBox = styled.div`
margin-top: 64px;
margin-right: 24px;
margin-left: 24px;
`

export const ChooseHeader = styled.h3`
font-family: Fraunces;
text-align: center;
font-size: 28px;
line-height: 1;
font-weight: 900;
color: #FEFCF7;
`

export const ChooseText = styled.p`
font-family: Barlow;
text-align: center;
font-size: 15px;
line-height: 1.67;
font-weight: 400;
color: #D9D9D9;

margin-top: 24px;
`
export const ChooseList = styled.ul`

`
